mapper.select:
select

from antibiotic
where species = #{species,jdbcType=VARCHAR} and growth_stage = #{growthStage,jdbcType=TINYINT}

mapper.update:
update antibiotic
set antibiotic_name = #{antibioticName,jdbcType=VARCHAR},
  species = #{species,jdbcType=VARCHAR},
  growth_stage = #{growthStage,jdbcType=TINYINT},
  dose = #{dose,jdbcType=INTEGER}
where antibiotic_id = #{antibioticId,jdbcType=BIGINT}
